package Conditions_3;

public class EvenNumber_ourexp {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		for(int a=1;a<10;a++) {
			
			System.out.println(a);
		}
		System.out.println("*************Reverse****************");
		
		for(int a=10;a>=0;a--) {
			System.out.println(a);
		
		}
	}
}
		

	


