package Conditions_3;

public class IfEvenNumber9 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		/*
		 * num%2==0 ---> even else odd
		 */
		int num1=2;
		if(!(num1%13==0)) {
			System.out.println("Number is even");
		}else {
			System.out.println("Number is odd");
		}

	}

}
