package StaticG.Veriyable;

public class G_Veriyable3 {
	//static global variable
		static int empId;//default value
		static double salary;//default value
		static char grade;//default value
		static boolean isPrime;//default value

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println("Program Starts....");
		System.out.println("EmpId: "+G_Veriyable3.empId);//
		System.out.println("Salary: "+G_Veriyable3.salary);//
		System.out.println("Grade: "+G_Veriyable3.grade);// 
		System.out.println("Prime: "+G_Veriyable3.isPrime);//
		int age=25;//mandatory to initialize
		System.out.println(age);
		System.out.println("Program Ends...");

	}

}
